contracts/interfaces/IIPAccountRegistry..content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;

/// @title Interface for IP Account Registry
/// @notice This interface manages the registration and tracking of IP Accounts
/// @dev This interface is cloned from Story protocal's github on commit:
/// @dev https://github.com/storyprotocol/protocol-core-v1/commit/c71449417b0f369aee5fd293580b7e68007be914
interface IIPAccountRegistry {
    /// @notice Event emitted when a new IP Account is created
    /// @param account The address of the new IP Account
    /// @param implementation The address of the IP Account implementation
    /// @param chainId The chain ID where the token contract was deployed
    /// @param tokenContract The address of the token contract associated with the IP Account
    /// @param tokenId The ID of the token associated with the IP Account
    event IPAccountRegistered(
        address indexed account,
        address indexed implementation,
        uint256 indexed chainId,
        address tokenContract,
        uint256 tokenId
    );

    /// @notice Returns the IPAccount implementation address
    function IP_ACCOUNT_IMPL() external view returns (address);

    /// @notice Returns the IPAccount salt
    function IP_ACCOUNT_SALT() external view returns (bytes32);

    /// @notice Returns the public ERC6551 registry address
    function ERC6551_PUBLIC_REGISTRY() external view returns (address);

    /// @notice Deploys an IPAccount contract with the IPAccount implementation and returns the address of the new IP
    /// @dev The IPAccount deployment deltegates to public ERC6551 Registry
    /// @param chainId The chain ID where the IP Account will be created
    /// @param tokenContract The address of the token contract to be associated with the IP Account
    /// @param tokenId The ID of the token to be associated with the IP Account
    /// @return ipAccountAddress The address of the newly created IP Account
    function registerIpAccount(
        uint256 chainId,
        address tokenContract,
        uint256 tokenId
    ) external returns (address);

    /// @notice Returns the IPAccount address for the given NFT token.
    /// @param chainId The chain ID where the IP Account is located
    /// @param tokenContract The address of the token contract associated with the IP Account
    /// @param tokenId The ID of the token associated with the IP Account
    /// @return ipAccountAddress The address of the IP Account associated with the given NFT token
    function ipAccount(
        uint256 chainId,
        address tokenContract,
        uint256 tokenId
    ) external view returns (address);

    /// @notice Returns the IPAccount implementation address.
    /// @return The address of the IPAccount implementation
    function getIPAccountImpl() external view returns (address);
}

contracts/interfaces/IIPAssetRegistry.so.content:
// SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.20;

import {IIPAccountRegistry} from "./IIPAccountRegistry.sol";

/// @title Interface for IP Account Registry
/// @notice This interface manages the registration and tracking of IP Accounts
/// @dev This interface is cloned from Story protocal's github on commit:
/// @dev https://github.com/storyprotocol/protocol-core-v1/commit/d9e60b3ce22ccb81f412a310ff7add76f3303a1d
interface IIPAssetRegistry is IIPAccountRegistry {
    /// @notice Emits when an IP is officially registered into the protocol.
    /// @param ipId The canonical identifier for the IP.
    /// @param chainId The chain identifier of where the IP resides.
    /// @param tokenContract The token contract address of the IP NFT.
    /// @param tokenId The token identifier of the IP.
    /// @param name The name of the IP.
    /// @param uri The URI of the IP.
    /// @param registrationDate The date and time the IP was registered.
    event IPRegistered(
        address ipId,
        uint256 indexed chainId,
        address indexed tokenContract,
        uint256 indexed tokenId,
        string name,
        string uri,
        uint256 registrationDate
    );

    /// @notice Tracks the total number of IP assets in existence.
    function totalSupply() external view returns (uint256);

    /// @notice Registers an NFT as an IP asset.
    /// @param chainid The chain identifier of where the IP NFT resides.
    /// @param tokenContract The address of the NFT.
    /// @param tokenId The token identifier of the NFT.
    /// @return id The address of the newly registered IP.
    function register(
        uint256 chainid,
        address tokenContract,
        uint256 tokenId
    ) external returns (address id);

    /// @notice Gets the canonical IP identifier associated with an IP NFT.
    /// @dev This is equivalent to the address of its bound IP account.
    /// @param chainId The chain identifier of where the IP resides.
    /// @param tokenContract The address of the IP.
    /// @param tokenId The token identifier of the IP.
    /// @return ipId The IP's canonical address identifier.
    function ipId(
        uint256 chainId,
        address tokenContract,
        uint256 tokenId
    ) external view returns (address);

    /// @notice Checks whether an IP was registered based on its ID.
    /// @param id The canonical identifier for the IP.
    /// @return isRegistered Whether the IP was registered into the protocol.
    function isRegistered(address id) external view returns (bool);
}

contracts/IPAssetRegistrarOApp.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@layerzerolabs/lz-evm-oapp-v2/contracts/oapp/OApp.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interfaces/IIPAssetRegistry.sol";
import "./SimpleIpNft.sol";

struct MintData {
    address collectionAddress;
    address to;
    string uri;
}

/// @notice This OApp allows users to mint a NFT on a remote chain and register it with the IPAssetRegistry of Story Protocol.
/// @dev All allowd NFT collections must be owned by this OApp in order to authorize minting.
contract IPAssetRegistrarOApp is OApp {
    // The allowed NFT collection contract address
    mapping(address => bool) public allowedCollections;

    // The address of IPAssetRegistry contract
    address public ipAssetRegistry;

    // The Id of chain
    uint256 public chainId;

    // Current tokenID
    uint256 public tokenId;

    MintData public currentMintData;

    address public newlyRegisteredIp;

    constructor(
        address _endpoint,
        address _owner
    ) OApp(_endpoint, _owner) Ownable(msg.sender) {}

    /// @notice Allows the owner to add a new NFT collection contract to the allowed list.
    /// @param _collection The address of the NFT collection contract.
    /// @param _allowed The boolean value to allow or disallow minting from the collection.
    function setAllowedCollection(
        address _collection,
        bool _allowed
    ) external onlyOwner {
        allowedCollections[_collection] = _allowed;
    }

    /// @notice Allows the owner to set the IPAssetRegistry contract address.
    /// @param _ipAssetRegistry The address of the IPAssetRegistry contract.
    /// @param _chainId The Id of chain.
    function setIpAssetRegistry(
        address _ipAssetRegistry,
        uint256 _chainId
    ) external onlyOwner {
        ipAssetRegistry = _ipAssetRegistry;
        chainId = _chainId;
    }

    /// @notice Sends a message from the source to destination chain.
    /// @param _dstEid The destination chain's endpoint ID.
    /// @param _mintData The data of Nft. A structure includes: collection address, the recipient address, and the URI of the NFT.
    /// @param _options The message execution options (e.g., gas to use on destination).
    function send(
        uint32 _dstEid,
        MintData calldata _mintData,
        bytes calldata _options
    ) external payable {
        bytes memory _payload = encodeData(_mintData); // Encodes message as bytes.
        _lzSend(
            _dstEid, // Destination chain's endpoint ID.
            _payload, // Encoded message payload being sent.
            _options, // Message execution options (e.g., gas to use on destination).
            MessagingFee(msg.value, 0), // Fee struct containing native gas and ZRO token.
            payable(msg.sender) // The refund address in case the send call reverts.
        );
    }

    /// @notice Quotes the gas needed to pay for the full omnichain transaction in native gas or ZRO token.
    /// @param _dstEid Destination chain's endpoint ID.
    /// @param _mintData The data of Nft. A structure includes: collection address, the recipient address, and the URI of the NFT.
    /// @param _options Message execution options (e.g., for sending gas to destination).
    function quote(
        uint32 _dstEid,
        MintData calldata _mintData,
        bytes calldata _options
    ) public view returns (uint256 nativeFee, uint256 lzTokenFee) {
        bytes memory _payload = encodeData(_mintData); // Encodes message as bytes.

        MessagingFee memory fee = _quote(_dstEid, _payload, _options, false);
        return (fee.nativeFee, fee.lzTokenFee);
    }

    /// @notice Internal function override to handle incoming messages from another chain.
    /// @dev _origin A struct containing information about the message sender.
    /// @dev _guid A unique global packet identifier for the message.
    /// @param payload The encoded message payload being received.
    /// @dev The following params are unused in the current implementation of the OApp.
    /// @dev _executor The address of the Executor responsible for processing the message.
    /// @dev _extraData Arbitrary data appended by the Executor to the message.
    function _lzReceive(
        Origin calldata /* _origin */,
        bytes32 /* _guid */,
        bytes calldata payload,
        address /* _executor */,
        bytes calldata /* _extraData */
    ) internal override {
        // Decode the payload data to get the MintData struct.
        MintData memory _mintData = abi.decode(payload, (MintData));

        // Mint the NFT on the allowed collection.
        require(
            allowedCollections[_mintData.collectionAddress],
            "Collection not allowed"
        );
        uint256 newTokenId = SimpleIpNft(_mintData.collectionAddress).safeMint(
            _mintData.to,
            _mintData.uri
        );

        currentMintData = _mintData;
        tokenId = newTokenId;

        // Register the NFT with the IPAssetRegistry.
        newlyRegisteredIp = IIPAssetRegistry(ipAssetRegistry).register(
            chainId,
            _mintData.collectionAddress,
            tokenId
        );
    }

    /// @notice The process of creating payload data for the message will be handled in this function.
    /// @dev The params of this function can be modified as per the requirements of the OApp.
    function encodeData(
        MintData calldata _mintData
    ) internal pure returns (bytes memory) {
        return abi.encode(_mintData);
    }
}

contracts/SimpleIpNft.sol.content:
// SPDX-License-Identifier: MIT
// Compatible with OpenZeppelin Contracts ^5.0.0
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract SimpleIpNft is ERC721, ERC721URIStorage, Ownable {
    uint256 private _nextTokenId;

    constructor(
        address initialOwner
    ) ERC721("Simple IP NFT", "SIM") Ownable(initialOwner) {}

    function safeMint(
        address to,
        string memory uri
    ) public onlyOwner returns (uint256 tokenId) {
        tokenId = _nextTokenId++;
        _safeMint(to, tokenId);
        _setTokenURI(tokenId, uri);
    }

    // The following functions are overrides required by Solidity.

    function tokenURI(
        uint256 tokenId
    ) public view override(ERC721, ERC721URIStorage) returns (string memory) {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override(ERC721, ERC721URIStorage) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

